package com;

public class arithOperators {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		
		//CONCETINATION
		
		
		System.out.println("Hello" +20+23); //Hello2023
		System.out.println(20+10+"Hello");  //30Hello
		
		System.out.println("Hello" +'A'+'B'); //HelloAB
		
		System.out.println('A'+'B'); // 131 using octa-decimal
		
		System.out.println(20+'A');  // 85
		
		System.out.println("================================"); 
		
		System.out.println(5/2); //2
		
		System.out.println(5/2.0); //2.5 
		
		System.out.println(3/2); //1 
		
		System.out.println(3/2.0);  //1.5
		
		System.out.println(0/2);  // 0 
		
	     System.out.println(5/0.0); // infinity
	     
	     System.out.println(5.0/0); //infinity
		
		System.out.println(5/0);  // ERROR--->>>> ArithmeticException
		
		
		

	}

}
